-- Cria tabela fato com 4 partição--DROP TABLE TB_FATO;CREATE TABLE TB_FATO (	    ID_CLIENTE   VARCHAR2(20) NOT NULL,     ID_PRODUTO   VARCHAR2(20) NOT NULL, 	  ID_CANAL     VARCHAR2(20) NOT NULL, 	  ID_DATA      VARCHAR2(20) NOT NULL,	  VALOR_VENDA  NUMBER(9,2)  NOT NULL,        CONSTRAINT TB_FATO_PK          PRIMARY KEY (ID_CLIENTE, ID_PRODUTO, ID_CANAL, ID_DATA))    PARTITION BY RANGE (ID_DATA)    (       PARTITION TBS_FATO_PART_T1 VALUES LESS THAN ('20170331') TABLESPACE TBS_FATO_PART_T1,       PARTITION TBS_FATO_PART_T2 VALUES LESS THAN ('20170630') TABLESPACE TBS_FATO_PART_T2,       PARTITION TBS_FATO_PART_T3 VALUES LESS THAN ('20170930') TABLESPACE TBS_FATO_PART_T3,       PARTITION TBS_FATO_PART_T4 VALUES LESS THAN ('20171231') TABLESPACE TBS_FATO_PART_T4    );               